import React, {useState} from 'react'
import classes from "./Counter"

const Counter = () => {
    const [counter, setcounter] = useState(0)

    return (
        <section className={classes["counter-section"]}>
        <button onClick = {()=>setcounter(counter + 1)}>+</button>
        <div>{counter}</div>
        <button onClick = {()=>setcounter(counter - 1)}>-</button>
        </section>
    )
}

export default Counter
