import React, { useState } from "react";
import Button from "./Button";

const ReactExercises = () => {
  const [show, setShow] = useState(true);
  const [value, setValue] = useState();
  const users = [
    { name: "Joanne", id: "1" },
    { name: "Linta", id: "2" },
    { name: "Maria", id: "3" },
  ];
  const username = users.map((user) => <li key={user.id}>{user.name}</li>);
  return (
    <div>
      {/* SHOW LIST OF NAMES */}
      <ul>{username}</ul>

      {/* HIDE AND DISPLAY ELEMENTS */}
      <Button onClick={() => setShow(!show)}>
        {show ? "Hide Text Below" : "Display Text"}
      </Button>
      {show && <div>Hello World!!!</div>}

      {/* TWO WAY DATA BINDING */}
      <form>
        <input onInput={(e) => setValue(e.target.value)}></input>
      </form>
      <div>{value}</div>

      {/* DISABLE BUTTON CONDITIONALLY */}
      <form>
        <input onInput={(e) => setValue(e.target.value)}></input>
      </form>
      <button disabled={value.length < 1}>Submit</button>
    </div>
  );
};

export default ReactExercises;
